// NFTDutchAuction.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract NFTDutchAuction {
    using SafeMath for uint256;

    address payable public seller;
    IERC721 public nftToken;
    uint256 public nftTokenId;
    uint256 public reservePrice;
    uint256 public numBlocksAuctionOpen;
    uint256 public offerPriceDecrement;
    uint256 public initialPrice;
    uint256 public auctionStartTime;
    uint256 public auctionEndTime;
    uint256 public currentPrice;
    bool public auctionEnded;

    modifier onlyBuyer() {
        require(msg.sender != seller, "Only buyer can call this function");
        _;
    }

    modifier auctionOpen() {
        require(block.number >= auctionStartTime && block.number < auctionEndTime, "Auction is not open");
        _;
    }

    constructor(
        address _nftTokenAddress,
        uint256 _nftTokenId,
        uint256 _reservePrice,
        uint256 _numBlocksAuctionOpen,
        uint256 _offerPriceDecrement
    ) {
        seller = payable(msg.sender);
        nftToken = IERC721(_nftTokenAddress);
        nftTokenId = _nftTokenId;
        reservePrice = _reservePrice;
        numBlocksAuctionOpen = _numBlocksAuctionOpen;
        offerPriceDecrement = _offerPriceDecrement;

        auctionStartTime = block.number;
        auctionEndTime = auctionStartTime + numBlocksAuctionOpen;

        initialPrice = reservePrice.add(offerPriceDecrement.mul(numBlocksAuctionOpen - 1));
        currentPrice = initialPrice;
        auctionEnded = false;
    }

    function bid() external payable onlyBuyer auctionOpen {
        require(msg.value >= currentPrice, "Bid amount is lower than the current price");

        if (block.number >= auctionEndTime) {
            auctionEnded = true;
            revert("Auction has already ended");
        }

        seller.transfer(currentPrice);
        nftToken.safeTransferFrom(address(this), msg.sender, nftTokenId);

        auctionEnded = true;
    }
}

